from django.urls import pathfrom django.contrib.auth import views as auth_views, authenticatefrom . import viewsfrom .forms import CustomLoginFormfrom django.contrib.auth.views import LoginViewfrom django.views.generic.base import RedirectViewurlpatterns = [    # Ruta de la pagina principal (HOME)    path('', views.home, name='home'),    # URLs para las vistas AJAX de las ciudades y departamentos    path('ajax/load-regions/', views.load_regions, name='ajax_load_regions'),    path('ajax/load-cities/', views.load_cities, name='ajax_load_cities'),    # Ruta para el registro de usuarios    path('register/', views.register, name='register'),    #reseteo de contraseñas    path('password_reset/', auth_views.PasswordResetView.as_view(), name='password_reset'),    path('password_reset_done/', auth_views.PasswordResetDoneView.as_view(), name='password_reset_done'),    path('reset/<uidb64>/<token>/', auth_views.PasswordResetConfirmView.as_view(), name='password_reset_confirm'),    path('reset_done/', auth_views.PasswordResetCompleteView.as_view(), name='password_reset_complete'),    # Ruta para el inicio de sesión utilizando LoginView    path('login/', LoginView.as_view(template_name='attendance/login.html', authentication_form=CustomLoginForm),         name='login'),    path('mark-attendance/', views.mark_attendance, name='mark-attendance'),    path('logout/', auth_views.LogoutView.as_view(next_page='login'), name='logout'),    # Conteo de asistencias    path('attendance-summary/', views.attendance_summary, name='attendance_summary'),]